const AddBtn = document.querySelector(".add-doctor")
const LoginForm = document.querySelector(".login-page")
const EditPage = document.querySelector(".edit1")
const X = document.querySelector(".fa-circle-xmark")
const X2 = document.querySelector(".xmark2")
const Edit = document.querySelector(".fa-pen")
const Trash = document.querySelector(".fa-trash")
const modal = document.querySelector(".removecardP")
const DeleteUser = document.querySelector(".DeleteUser")
const DontDeleteUser = document.querySelector(".DontDeleteUser")

AddBtn.addEventListener("click", () => {
    LoginForm.classList.add("DisplayForm")
})

X.addEventListener("click", () => {
    LoginForm.classList.remove("DisplayForm")
})

X2.addEventListener("click", () => {
    EditPage.classList.add("edit-page")
})
// show the edit page  
Edit.addEventListener("click", () => {
    EditPage.classList.remove("edit-page")
})

// show and remove the delete modal
Trash.addEventListener("click", () => {
    modal.classList.add("show-card")
})

// remove delete user page from Delete button
DeleteUser.addEventListener("click", () => {
    modal.classList.remove("show-card")
})

// remove delete user page from xmark 
DontDeleteUser.addEventListener("click", () => {
    modal.classList.remove("show-card")
})

